SELECT B.BOARD_IDX, B.CTG_CODE, B.TITLE, B.READ_CNT, B.AGREE_CNT, B.REG_DATE, COUNT(C.COMMENT_IDX) as COMMENT_COUNT FROM BOARD_MUZZIMA B LEFT JOIN COMMENT C ON B.BOARD_IDX = C.BOARD_IDX WHERE B.REG_DATE >= DATE_SUB(NOW(), INTERVAL 24 HOUR) GROUP BY B.BOARD_IDX, B.CTG_CODE, B.TITLE, B.READ_CNT, B.AGREE_CNT, B.REG_DATE ORDER BY (B.READ_CNT + B.AGREE_CNT * 2 + COUNT(C.COMMENT_IDX) * 3) DESC LIMIT 5